import { createGlobalStyle } from "styled-components";
//O comportamento do estylo da pagina
const Global = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    font-family: sans-serif;
    color: blue;
  }
  
  body {
    margin-left: 500px;  
    width: 100%;
   
   
  }
  `;

export default Global;
